import{_ as l,c as i,o as e,a7 as t}from"./chunks/framework.DENnfyYs.js";const d=JSON.parse('{"title":"大纲笔记","description":"","frontmatter":{},"headers":[],"relativePath":"Inbox/vitepress/pages/outline-notes.md","filePath":"Inbox/vitepress/pages/outline-notes.md","lastUpdated":1709689572000}'),r={name:"Inbox/vitepress/pages/outline-notes.md"},a=t('<h1 id="大纲笔记" tabindex="-1">大纲笔记 <a class="header-anchor" href="#大纲笔记" aria-label="Permalink to &quot;大纲笔记&quot;">​</a></h1><p><span class="fgray">在 vitepress 中使用大纲做笔记。</span></p><h2 id="案例展示" tabindex="-1">案例展示 <a class="header-anchor" href="#案例展示" aria-label="Permalink to &quot;案例展示&quot;">​</a></h2><ul><li>我的笔记放在哪？ <ul><li>了解笔记分类 <ul><li>PARA</li><li>中国图书分类法</li></ul></li><li>我的分类（改造后的 PARA）： <ul><li>inbox：存放所有未整理的笔记，所有新建的笔记都存放于此。</li><li>images：存放图片，如：软件配置相关的图片等</li><li>Project：存放持续更新的项目，如：Go 语言学习、操作系统、IT-Tips</li><li>Archive：存放已完结的项目，如：上面的 Go 语言学习我已经学完并归纳总结了，我就将其移动到此。</li><li>Home：是一个 MOC 汇总文件，里面记录着 Project 以及 Archive 下的每个项目的汇总 MOC</li></ul></li></ul></li><li>如何找到我想要的笔记？ <ul><li>一般用 ==搜索关键字== 来找到我想要的笔记</li><li>通过索引笔记 （MOC）进行漫游 <ul><li>我会在每个 Project 的根目录创建一个 MOC，如：在 Go 语言学习根目录下创建 Go-MOC</li><li><blockquote><p>测试引用</p></blockquote></li><li>Go-MOC 里记录着： <ul><li>Go-基础 <ul><li>数据类型</li><li>函数</li><li>指针</li></ul></li><li>Go-高级</li><li>Go-进阶</li><li>Go-项目实战</li></ul></li><li>在 Home 入口文件中通过双向链接链接上面的 Go-MOC、IT-Tips-MOC 等 <ol><li>CFA 刷题一套</li><li>阅读完《配色设计原理》</li><li>......</li></ol></li></ul></li></ul></li><li>思考： <ul><li>否需要标签？ <ul><li>我个人很少用标签，因为我用 双向链接 代替了标签</li><li>如果要使用标签，建议用一个文件记录已经存在的标签，这样可避免添加相同语意的标签而造成标签混乱</li></ul></li><li>是否需要文件夹？ <ul><li>我只创建以每个项目名命名的文件夹，不再创建其子文件夹了，所有与这个项目有关的文件经过整理后会存放在这个项目文件夹中，归档后移动到 Archive 中。</li></ul></li><li><a href="https://forum-zh.obsidian.md/t/topic/20" target="_blank" rel="noreferrer">关于是否需要文件夹与标签的讨论</a></li></ul></li><li>推荐阅读 <ul><li><a href="https://zhuanlan.zhihu.com/p/353521308" target="_blank" rel="noreferrer">如何构建笔记系统 -实际操作篇</a></li><li><a href="https://www.zhihu.com/question/23427617/answer/1461195696" target="_blank" rel="noreferrer">如何构建笔记系统</a></li><li><a href="https://www.jianshu.com/p/9f8d43924209" target="_blank" rel="noreferrer">举一个拆书的实例来演示MOC创建的全过程</a></li><li><a href="https://b23.tv/rSWuMS6" target="_blank" rel="noreferrer">Struggle with Me：学术研究卡片笔记踩坑经验-哔哩哔哩</a></li><li><a href="https://b23.tv/u26OzOc" target="_blank" rel="noreferrer">越写越爽的笔记方法！迅速进入心流状态的“无摩擦”笔记法-哔哩哔哩</a></li><li><a href="https://forum.obsidian.md/t/ideaverse-for-obsidian-formerly-the-lyt-kit-now-available/390" target="_blank" rel="noreferrer">LYT Kit</a></li></ul></li></ul>',4),o=[a];function s(n,u,h,c,p,_){return e(),i("div",null,o)}const b=l(r,[["render",s]]);export{d as __pageData,b as default};
